// src/services/api.js - Hata y√∂netimini iyile≈ütirelim
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000,
});

// Request interceptor - t√ºm istekleri loglayalƒ±m
api.interceptors.request.use(
  (config) => {
    console.log(`üîÑ API ƒ∞stek: ${config.method?.toUpperCase()} ${config.url}`);
    return config;
  },
  (error) => {
    console.error('‚ùå API ƒ∞stek Hatasƒ±:', error);
    return Promise.reject(error);
  }
);
// ... (axios create ve interceptor'lar aynƒ± kalacak)

export const locationsAPI = {
  getAll: async () => {
    const response = await api.get('/locations');
    return response.data; // Deƒüi≈üiklik: Sadece veriyi d√∂nd√ºr
  },

  create: async (locationData) => {
    const response = await api.post('/locations', locationData);
    return response.data; // Bu zaten doƒüruydu
  },

  delete: async (id) => {
    const response = await api.delete(`/locations/${id}`);
    return response.data; // Deƒüi≈üiklik: Sadece veriyi d√∂nd√ºr
  },

  update: async (id, locationData) => {
    const response = await api.put(`/locations/${id}`, locationData);
    return response.data; // Deƒüi≈üiklik: Sadece veriyi d√∂nd√ºr
  },
};

export default api;
