import React, { useState } from 'react';

function AddLocationForm({ onAdd }) {
  const [form, setForm] = useState({
    name: '',
    description: '',
    lat: '',
    lng: '',
    visit_date: '',
    category: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAdd(form);
    setForm({ name: '', description: '', lat: '', lng: '', visit_date: '', category: '' });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" placeholder="Yer adı" value={form.name} onChange={handleChange} required />
      <input name="description" placeholder="Açıklama" value={form.description} onChange={handleChange} />
      <input name="lat" placeholder="Enlem" value={form.lat} onChange={handleChange} required />
      <input name="lng" placeholder="Boylam" value={form.lng} onChange={handleChange} required />
      <input name="visit_date" type="date" value={form.visit_date} onChange={handleChange} />
      <input name="category" placeholder="Kategori" value={form.category} onChange={handleChange} />
      <button type="submit">Ekle</button>
    </form>
  );
}

export default AddLocationForm;
