import React, { useEffect, useState } from 'react';
import { fetchLocations, addLocation, deleteLocation } from './services/api';
import LocationList from './components/LocationList';
import AddLocationForm from './components/AddLocationForm';
import './App.css';

function App() {
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    loadLocations();
  }, []);

  const loadLocations = async () => {
    const data = await fetchLocations();
    setLocations(data);
  };

  const handleAdd = async (loc) => {
    await addLocation(loc);
    loadLocations();
  };

  const handleDelete = async (id) => {
    await deleteLocation(id);
    loadLocations();
  };

  return (
    <div className="App">
      <h1>üåç Our Places</h1>
      <AddLocationForm onAdd={handleAdd} />
      <LocationList locations={locations} onDelete={handleDelete} />
    </div>
  );
}

export default App;
