import React, { useState, useEffect } from 'react';
import { X, MapPin } from 'lucide-react';

const AddLocationForm = ({ isOpen, onClose, onAddLocation, initialCoords }) => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    lat: '',
    lng: '',
    visit_date: new Date().toISOString().split('T')[0],
    category: 'romantic'
  });

  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  // YENƒ∞: Haritadan gelen koordinatlarƒ± forma doldur
  useEffect(() => {
    if (initialCoords) {
      setFormData(prev => ({
        ...prev,
        lat: initialCoords.lat.toFixed(6), // daha temiz bir g√∂r√ºn√ºm i√ßin
        lng: initialCoords.lng.toFixed(6)
      }));
    }
  }, [initialCoords]);

  const popularCities = [
    { name: 'ƒ∞stanbul (Taksim)', lat: 41.0351, lng: 28.9833 },
    { name: 'ƒ∞stanbul (Kadƒ±k√∂y)', lat: 40.9927, lng: 29.0289 },
    { name: 'Ankara (Kƒ±zƒ±lay)', lat: 39.9208, lng: 32.8541 },
    { name: 'ƒ∞zmir (Alsancak)', lat: 38.4362, lng: 27.1418 },
    { name: 'Bursa (Merkez)', lat: 40.1825, lng: 29.0669 },
    { name: 'Antalya (Lara)', lat: 36.8517, lng: 30.8487 },
    { name: 'Eski≈üehir (Merkez)', lat: 39.7767, lng: 30.5206 }
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleCitySelect = (city) => {
    setFormData(prev => ({
      ...prev,
      lat: city.lat.toString(),
      lng: city.lng.toString()
    }));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Mekan adƒ± gereklidir';
    }

    if (!formData.lat || !formData.lng) {
      newErrors.coordinates = 'Koordinatlar gereklidir';
    } else if (isNaN(parseFloat(formData.lat)) || isNaN(parseFloat(formData.lng))) {
      newErrors.coordinates = 'Ge√ßerli koordinatlar giriniz';
    }

    if (!formData.visit_date) {
      newErrors.visit_date = 'Ziyaret tarihi gereklidir';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setLoading(true);

    try {
      const success = await onAddLocation({
        ...formData,
        lat: parseFloat(formData.lat),
        lng: parseFloat(formData.lng)
      });

      if (success) {
        // Formu temizle ve kapat
        handleClose();
      }
    } catch (error) {
      console.error('Form g√∂nderilirken hata:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    setFormData({
      name: '',
      description: '',
      lat: '',
      lng: '',
      visit_date: new Date().toISOString().split('T')[0],
      category: 'romantic'
    });
    setErrors({});
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal open">
      <div className="modal-content">
        <div className="modal-header">
          <h2>üìç Yeni Yer Ekle</h2>
          <button className="close-btn" onClick={handleClose}>
            <X size={24} />
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          {/* Formun geri kalanƒ± aynƒ±, deƒüi≈üiklik yok */}
          <div className="form-group">
            <label htmlFor="name">Mekan Adƒ± *</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              placeholder="√ñrn: ƒ∞stiklal Caddesi, Emin√∂n√º..."
              disabled={loading}
            />
            {errors.name && <span className="error">{errors.name}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="description">A√ßƒ±klama</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              placeholder="Burada neler ya≈üadƒ±nƒ±z? √ñzel anƒ±larƒ±nƒ±z..."
              disabled={loading}
              rows="3"
            />
          </div>

          <div className="form-group">
            <label>Hƒ±zlƒ± Se√ßim (T√ºrkiye ≈ûehirleri)</label>
            <div className="city-buttons">
              {popularCities.map(city => (
                <button
                  key={city.name}
                  type="button"
                  className="city-btn"
                  onClick={() => handleCitySelect(city)}
                  disabled={loading}
                >
                  <MapPin size={12} />
                  {city.name}
                </button>
              ))}
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="lat">Enlem (Latitude) *</label>
              <input
                type="text"
                id="lat"
                name="lat"
                value={formData.lat}
                onChange={handleInputChange}
                placeholder="41.0082"
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label htmlFor="lng">Boylam (Longitude) *</label>
              <input
                type="text"
                id="lng"
                name="lng"
                value={formData.lng}
                onChange={handleInputChange}
                placeholder="28.9784"
                disabled={loading}
              />
            </div>
          </div>

          {errors.coordinates && (
            <span className="error" style={{display: 'block', marginTop: '-10px', marginBottom: '15px'}}>
              {errors.coordinates}
            </span>
          )}

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="visit_date">Ziyaret Tarihi *</label>
              <input
                type="date"
                id="visit_date"
                name="visit_date"
                value={formData.visit_date}
                onChange={handleInputChange}
                disabled={loading}
              />
              {errors.visit_date && <span className="error">{errors.visit_date}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="category">Kategori</label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleInputChange}
                disabled={loading}
              >
                <option value="romantic">üíñ Romantik</option>
                <option value="fun">üòÑ Eƒülence</option>
                <option value="food">üçï Yemek</option>
                <option value="culture">üé® K√ºlt√ºr</option>
                <option value="adventure">üèûÔ∏è Macera</option>
              </select>
            </div>
          </div>

          <button 
            type="submit" 
            className="submit-btn"
            disabled={loading}
          >
            {loading ? 'Ekleniyor...' : 'üìç Yeri Ekle'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddLocationForm;
